const array = [1, 2, 3, 4];

// map takes an array and transforms it into a new array
const mappedArray = array.map(x => x * 2);

// reduce takes an array and reduces it to a single value
const reducedValue = mappedArray.reduce((accumulator, currentValue) => {
  // accumulator is the running total of the reduction
  // currentValue is the current element being processed in the array
  return accumulator + currentValue;
}, 0); // 0 is the initial value of the accumulator

console.log(reducedValue); // 20
